private void serialEnabledFldActionPerformed(java.awt.event.ActionEvent evt) {                                               
        
    	//System.out.println("The event is: "+ evt);
    	//System.out.println("The entered port number is: "+ Integer.parseInt(evt.getActionCommand()));
    if(serialEnabled.isSelected()){	
    	CrossMapper_V0_1_Start.myMapperV3.serialEnabled =true;
    	CrossMapper_V0_1_Start.myMapperV3.serialInitialise();
    	System.out.println("Baud rate is: "+ CrossMapper_V0_1_Start.myMapperV3.baudRate);
        baudRate.setText(Integer.toString(CrossMapper_V0_1_Start.myMapperV3.baudRate));
        portName.setText(CrossMapper_V0_1_Start.myMapperV3.comPort);
    }
    else{
    	if (CrossMapper_V0_1_Start.myMapperV3.serialEnabled =true){;
    	CrossMapper_V0_1_Start.myMapperV3.serialStop();
    	CrossMapper_V0_1_Start.myMapperV3.serialEnabled =false;
    	baudRate.setText(Integer.toString(CrossMapper_V0_1_Start.myMapperV3.baudRate));
        portName.setText(CrossMapper_V0_1_Start.myMapperV3.comPort);
    	}
    }
    } 

private void baudRateFldActionPerformed(ActionEvent evt) {
	// TODO Auto-generated method stub
	System.out.println("The entered Baud Rate is: "+ Integer.parseInt(evt.getActionCommand()));
	if(serialEnabled.isSelected()){
		CrossMapper_V0_1_Start.myMapperV3.serialStop();
		CrossMapper_V0_1_Start.myMapperV3.serialBaudChanged(Integer.parseInt(evt.getActionCommand()));
		CrossMapper_V0_1_Start.myMapperV3.serialInitialise();
		
}
	else{
		CrossMapper_V0_1_Start.myMapperV3.serialBaudChanged(Integer.parseInt(evt.getActionCommand()));
	
	}
}

