static public void serialNotAvailable(){
	serialEnabled.setSelected(false);
	baudRate.setText(Integer.toString(CrossMapper_V0_1_Start.myMapperV3.baudRate));
    portName.setText(CrossMapper_V0_1_Start.myMapperV3.comPort);
	
}
   

